import React from 'react';

function HeaderNav() {
  // Estado para controlar se o menu está aberto ou fechado
  const [menuOpen, setMenuOpen] = useState(false);

  // Função que alterna o estado do menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className='HeaderNav'>
      {/* Ícone de hambúrguer */}
      <div className="menu-icon" onClick={toggleMenu}>
        &#9776; {/* Ícone de hambúrguer */}
      </div>

      {/* Menu de navegação, com classe dinâmica baseada no estado */}
      <nav className={`nav-links ${menuOpen ? 'open' : ''}`}>
        <a href="#BannerBox">QUEM SOMOS</a>
        <a href="#ServicosBox">SERVIÇOS</a>
        <a href="#agendeseuhorariobox">AGENDE SEU HORÁRIO</a>
        <a href="#redes">CONTATOS</a>
      </nav>
    </div>
  );
}

export default HeaderNav;
